import './App.css';

import React, { Component } from 'react';
import axios from 'axios';

import Profile from './Profile';

class App extends Component {
  constructor() {
    super();

    this.state = {
      userData: null
    }
  }

  componentDidMount() {
    axios.get('https://randomuser.me/api/')
      .then(data => {
        console.log(data);
        this.setState({
          userData: data.data.results[0]
        })
      })
      .catch(err => {
